settings.css:
:root {
  --dv-primary: #f00;
  --dv-primary-hover: #f55;
  --dv-primary-active: #a00;
}

:root {
  --bs-primary: var(--dv-primary) !important;
}

.btn-primary {
  --bs-btn-bg: var(--dv-primary) !important;
  --bs-btn-border-color: var(--dv-primary) !important;

  --bs-btn-hover-bg: var(--dv-primary-hover) !important;
  --bs-btn-hover-border-color: var(--dv-primary-hover) !important;

  --bs-btn-active-bg: var(--dv-primary-active) !important;
  --bs-btn-active-border-color: var(--dv-primary-active) !important;
}
.btn-outline-primary {
  --bs-btn-color: var(--dv-primary) !important;
  --bs-btn-border-color: var(--dv-primary) !important;

  --bs-btn-hover-bg: var(--dv-primary) !important;
  --bs-btn-hover-border-color: var(--dv-primary) !important;

  --bs-btn-active-bg: var(--dv-primary-active) !important;
  --bs-btn-active-border-color: var(--dv-primary-active) !important;
}
.btn-link {
  --bs-btn-color: var(--dv-primary) !important;

  --bs-btn-hover-color: var(--dv-primary-hover) !important;

  --bs-btn-active-color: var(--dv-primary-active) !important;
}
.text-primary {
    color: var(--bs-primary) !important;
}
.bg-primary {
    background-color: var(--bs-primary) !important;
}
.border-primary {
    border-color: var(--bs-primary) !important;
}
.polling{
    align-self: center;
}

